var open=document.querySelector(".button-letter"),popup=document.querySelector(".modal"),close=popup.querySelector(".feedback-close"),usernamefield=popup.querySelector("[name=usernamefield]"),email=popup.querySelector("[name=email]"),form=popup.querySelector("form");open.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),usernamefield.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),form.addEventListener("submit",function(e){usernamefield.value&&email.value||(e.preventDefault(),console.log("Нужно ввести Ваше имя и электронную почту"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&popup.classList.remove("modal-show"))});